import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import EmployeeService from "../services/EmployeeService";

function CreateEmployeeComponent() {
  const { empno } = useParams();   // <-- get :empno from URL
  const navigate = useNavigate();

  const [employee, setEmployee] = useState({
    firstName: "",
    lastName: "",
    emailId: ""
  });

  useEffect(() => {
    if (empno !== "_add") {
      EmployeeService.getEmployeeById(empno).then((res) => {
        setEmployee(res.data);
      });
    }
  }, [empno]);

  const saveOrUpdateEmployee = (e) => {
    e.preventDefault();
    if (empno === "_add") {
      EmployeeService.createEmployee(employee).then(() => navigate("/employees"));
    } else {
      EmployeeService.updateEmployee(employee, empno).then(() => navigate("/employees"));
    }
  };

  const cancel = () => navigate("/employees");

  return (
    <div className="container">
      <div className="card col-md-6 offset-md-3">
        <h3 className="text-center">{empno === "_add" ? "Add Employee" : "Update Employee"}</h3>
        <div className="card-body">
          <form>
            <div className="form-group">
              <label>First Name:</label>
              <input
                type="text"
                value={employee.firstName}
                onChange={(e) => setEmployee({ ...employee, firstName: e.target.value })}
                className="form-control"
              />
            </div>
            <div className="form-group">
              <label>Last Name:</label>
              <input
                type="text"
                value={employee.lastName}
                onChange={(e) => setEmployee({ ...employee, lastName: e.target.value })}
                className="form-control"
              />
            </div>
            <div className="form-group">
              <label>Email Id:</label>
              <input
                type="text"
                value={employee.emailId}
                onChange={(e) => setEmployee({ ...employee, emailId: e.target.value })}
                className="form-control"
              />
            </div>
            <button className="btn btn-success" onClick={saveOrUpdateEmployee}>Save</button>
            <button className="btn btn-danger" onClick={cancel} style={{ marginLeft: "10px" }}>Cancel</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default CreateEmployeeComponent;
